+  @PublishedApi
+  internal fun rawLog(priority: Int, tag: String?, throwable: Throwable?, message: String?) {
+    forestArray.forEach { it.rawLog(priority, tag, throwable, message) }
+  }
+
+  fun tagged(tag: String): Tree {
+    val taggedTag = tag
+    return object : Tree() {
